import { createContext} from 'react';
import { action, decorate, observable } from 'mobx';

export class PostStore {
    items = [
        'Foo',
        'Bar'
    ]

    reset () {
        this.items = ['Reset']
    }
}

decorate(PostStore, {
    item: observable,
    reset: action
})

export default createContext(new PostStore())
