import { observable, computed, action, decorate}  from 'mobx';

class MovieStore {
    movielist = [];

    get count() {
        return this.movielist.length;
    }

    set_movies(array) {
        this.movielist = array || [];
    }

    add_movies(object) {
        this.movielist.push(object);
    }
}

decorate(MovieStore, {
    movielist: observable,
    count: computed,
    set_movies: action,
    add_movies: action
});


const store = new MovieStore();
export default store;
