import {useEffect, useState } from 'react';
import localStorage from 'mobx-localstorage';

const Favorites = props => {

  const [data, setData] = useState([]);

  const [fav, setFav] = useState([]);

  useEffect(() => {
    const data = (JSON.parse(localStorage.getItem("data")));
    const favor = localStorage.getItem("fav");

    function filterData(data, filters) {
      console.log(data, filters);
      let favorite =  data.filter(({show}) => filters.includes(show.id));
      return favorite;
    }
    console.log(favor)

    setFav(favor);
    setData(filterData(data, favor)); 

    console.log(JSON.parse(localStorage.getItem('win')));

  }, [props]); // props triger

  return(
    <div className="container">
      <center>
        <div className="row">
        {result.map(search => (
        <div className="column" key={search.id} id="poster">
            <div className="card text-white bg-dark mb-3">
                <div className="card-header">{search.Title} ({search.Year})</div>
                  <div className="container">
                    <h4 className="card-title"></h4>
                    <img src={search.Poster} id="image"/>
                  </div>
                  <p class="card-text">{search.Type}</p>
                  <button onClick={() => addFavorite(search)}>Fav</button>
                  <button onClick={() => check(search)}>Ce</button>

            </div>
        </div>
        ))}
        </div>
      </center>
    <style jsx>{`
          .form-control {
            margin-right:12px;
          }
          #poster {
            padding: 0 10px;
          }
          .card {
            width:250px;
            height:420px;
            text-align: center;
          }
          @media screen and (max-width: 600px) {
          .column {
            width: 100%;
            display: block;
            margin-bottom: 20px;
          }
        }

        `}</style>
  </div>
  )};


export default Favorites;
