import {useEffect, useState } from 'react';
import localStorage from 'mobx-localstorage';


function Favorites(){

  const [result, setResult] = useState([]);

  useEffect(() => {
    if(localStorage.getItem('favorites')){
      setResult(JSON.parse(localStorage.getItem('favorites')));
    }
  }, []);

  return(
    <div className="container">
      <center>
        <div className="row">
        {result.map(search => (
        <div className="column" key={search.id} id="poster">
            <div className="card text-white bg-dark mb-3">
                <div className="card-header">{search.Title} ({search.Year})</div>
                  <div className="container">
                    <h4 className="card-title"></h4>
                    <img src={search.Poster} id="image"/>
                  </div>
                  <p class="card-text">{search.Type}</p>
            </div>
        </div>
        ))}
        </div>
      </center>
    <style jsx>{`
         #image {
            width: 100%;
            height: 300px
          }
          #poster {
            padding: 0 10px;
          }
          .card {
            width:250px;
            height:420px;
            text-align: center;
          }
          @media screen and (max-width: 600px) {
          .column {
            width: 100%;
            display: block;
            margin-bottom: 20px;
          }
        }

        `}</style>
  </div>
  )};


export default Favorites;
