import React, { useState, useEffect } from 'react';

function Card({index, item}) {

    const [favorite, setFavorite] = useState([]);
    const [favstatus, setFavStatus] = useState(true);

    if(Array.isArray(favorite)){
        let isFav = favorite.find(items => item.imdbID === items.imdbID);
        if(isFav && favstatus !== false) {
            setFavStatus(false);
        }
    }

    const addFavorite = object => {
        const newFav = [...favorite, object];
        setFavorite(newFav);
        if (typeof window !== 'undefined') {
            React.localStorage.setItem('favorites', JSON.stringify(newFav));
            console.log(JSON.parse(localStorage.getItem('favorites')));
        }
    };

    const delFavorite = index => {
        const newFav = [...favorite];
        newFav.splice(index, 1);
        setFavorite(newFav);

        React.localStorage.setItem('favorites', JSON.stringify(newFav));
        console.log(JSON.parse(localStorage.getItem('favorites')));

    };


    useEffect(() => {
        setFavorite(JSON.parse(React.localStorage.getItem('favorites')));
    }, []);

    return <div className="column" key={item.id} id="poster">
            <div className="card text-white bg-dark mb-3">
                <div className="card-header">{item.Title} ({item.Year})</div>
                  <div className="container">
                    <h4 className="card-title"></h4>
                    <img src={item.Poster} id="image"/>
                    <small>{item.Type} </small>
                  </div>
                  <p className="card-text">

                      <button className="btn-primary"style={{display:favstatus ? "":"none"}} onClick={() => addFavorite(item)}>
                        Favorite
                      </button>
                      <button className="btn-primary"style={{display:favstatus ? "none":""}} onClick={() => delFavorite(index)}>
                        Del Favorite
                      </button>
                  </p>

            </div>
            <style jsx>{`
                  .card-header {
                    font-size: 14px;
                  }
                  #image {
                    width: 100%;
                    height: 300px
                  }
                  #poster {
                    padding: 0 10px;
                  }
                  .card {
                    width:250px;
                    height:420px;
                    text-align: center;
                  }
                  @media screen and (max-width: 600px) {
                  .column {
                    width: 100%;
                    display: block;
                    margin-bottom: 20px;
                  }
                }
            `}
            </style>

        </div>;
}

export default Card;


