import fetch from 'isomorphic-unfetch';
import localStorage from 'mobx-localstorage';

const Index = props => (
    <div className="container">
        <form className="form-inline" id="form">
          <div className="row">
            <div className="column">
              <input type="text" className="form-control" placeholder="First name" />
            </div>
            <div className="column">
              <input type="text" className="form-control" placeholder="Last name" />
            </div>
            <div className="column">
              <select id="inputState" className="form-control">
                <option selected>Choose...</option>
                <option>...</option>
              </select>
            </div>
            <button type="submit" className="btn btn-info">Search</button>
          </div>
        </form>
        <hr/>
        <center>
        <div className="row">
        {props.shows.map(show => (
        <div className="column" key={show.id} id="poster">
            <div className="card text-white bg-primary mb-4">
                <div className="card-header">{show.name}
                    <h4 className="card-title">{show.type}</h4>
                    <img src={show.image.medium} />
                </div>
            </div>
        </div>
        ))}
        </div>
        </center>


        <style jsx>{`
          .form-control {
            margin-right:12px;
          }
          #poster {
            padding: 0 10px;
          }
          .card {
            width:250px;
            height:420px;
            text-align: center;
          }
          @media screen and (max-width: 600px) {
          .column {
            width: 100%;
            display: block;
            margin-bottom: 20px;
          }
        }

        `}</style>

    </div>

);


Index.getInitialProps = async function () {

  const res = await fetch('https://api.tvmaze.com/search/shows?q=batman');
  const data = await res.json();

  const fav = [975, 481, 504]
  console.log(`Show data fetched. Count: ${data.length}`);

  if (typeof window !== 'undefined') {
    localStorage.setItem('data', JSON.stringify(data));
    localStorage.setItem('fav', fav);
  }

  return {
    shows: data.map(entry => entry.show)
  };

}



export default Index;
