import localStorage from 'mobx-localstorage';
import React, { useState, useEffect } from 'react';

function Card({movie, index, addFavorite, delFavorite}) {


    const [favstatus, setFavStatus] = useState(true);



    return <div className="column" key={movie.id} id="poster">
            <div className="card text-white bg-dark mb-3">
                <div className="card-header">{movie.Title} ({movie.Year})</div>
                  <div className="container">
                    <h4 className="card-title"></h4>
                    <img src={movie.Poster} id="image"/>
                    <small>{movie.Type} </small>
                  </div>
                  <p className="card-text">

                      <button className="btn-primary"onClick={() => addFavorite(index)}>
                        Favorite
                      </button>
                      <button className="btn-primary" onClick={() => delFavorite(index)}>
                        Del Favorite
                      </button>
                  </p>

            </div>
            <style jsx>{`
                  .card-header {
                    font-size: 14px;
                  }
                  #image {
                    width: 100%;
                    height: 300px
                  }
                  #poster {
                    padding: 0 10px;
                  }
                  .card {
                    width:250px;
                    height:420px;
                    text-align: center;
                  }
                  @media screen and (max-width: 600px) {
                  .column {
                    width: 100%;
                    display: block;
                    margin-bottom: 20px;
                  }
                }
            `}
            </style>

        </div>;
}

export default Card;


