import localStorage from 'mobx-localstorage';
import React, { useState, useEffect } from 'react';

function Card({items}) {
    const [favorite, setFavorite] = useState([]);
    const [favstatus, setFavStatus] = useState(true);

    const check = object => {

        if(Array.isArray(favorite)){
          const isFav = favorite.find(item => object.imdbID === item.imdbID);
          if(isFav){
            setFavStatus(true);
          }
        }
        console.log(object.imdbID, favstatus, favorite);
    };

    useEffect(() => {
        if(localStorage.getItem('favorites')){
          setFavorite(JSON.parse(localStorage.getItem('favorites')));
        }
        if(Array.isArray(favorite)){
            const isFav = favorite.find(item => items.imdbID === item.imdbID);
            if(!isFav){
              setFavStatus(false);
            }
        }
    }, []);

    return <div className="column" key={item.id} id="poster">
            <div className="card text-white bg-dark mb-3">
                <div className="card-header">{item.Title} ({item.Year})</div>
                  <div className="container">
                    <h4 className="card-title"></h4>
                    <img src={item.Poster} id="image"/>
                  </div>
                  <p class="card-text">{item.Type}</p>
                  <button style={{display:favstatus ? "none":""}} onClick={() => check(item)}>Fav</button>
            </div>
            <style jsx>{`
                  .card-header {
                    font-size: 14px;
                  }
                  #image {
                    width: 100%;
                    height: 300px
                  }
                  #poster {
                    padding: 0 10px;
                  }
                  .card {
                    width:250px;
                    height:420px;
                    text-align: center;
                  }
                  @media screen and (max-width: 600px) {
                  .column {
                    width: 100%;
                    display: block;
                    margin-bottom: 20px;
                  }
                }
            `}
            </style>

        </div>;
}

export default Card;


