import fetch from 'isomorphic-unfetch';
import localStorage from 'mobx-localstorage';
import React, { useState, useEffect } from 'react';

function Index() {

  const [value, setValue] = useState();
  const [result, setResult] = useState([]);
  const [favorite, setFavorite] = useState([]);
  const [favstatus, setFavStatus] = useState(true);

  const handleSubmit = async e => {
    e.preventDefault();
    if(!value) return;
    const res = await fetch(`http://www.omdbapi.com/?s=${value}&apikey=8ae8b189`);
    const data = await res.json();


    data.Response === "True" ? setResult(data.Search) : alert("Movie not found!");

    console.log(data);

  }

  const check = object => {

    if(favorite){
      favorite.map(item => {
        if(item.imdbID !== object.imdbID){
          console.log(item.imdbID);
          setFavStatus(false);
        }
      });
    }
    console.log(object.imdbID, favstatus, favorite);
  };

  const addFavorite = object => {
    if(favstatus){
      const newFav = [...favorite, object];
      setFavorite(newFav);
      if (typeof window !== 'undefined') {
        localStorage.setItem('favorites', JSON.stringify(newFav));
        console.log(JSON.parse(localStorage.getItem('favorites')));
      }
    }
  };

  useEffect(() => {
    if(localStorage.getItem('favorites')){
      setFavorite(JSON.parse(localStorage.getItem('favorites')));
    }
  }, []);
    return (
      <div className="container">
        <form className="form-inline" id="form" onSubmit={handleSubmit}>
          <div className="row">
            <div className="column">
              <input type="text" className="form-control" placeholder="Title" onChange={e => setValue(e.target.value)}/>
            </div>
            <div className="column">
              <input type="text" className="form-control" placeholder="Year" />
            </div>
            <div className="column">
              <select id="inputState" className="form-control">
                <option selected>Choose...</option>
                <option>...</option>
              </select>
            </div>
            <button type="submit" className="btn btn-info">Search</button>
          </div>
        </form>
        <hr/>
        <center>
        <div className="row">
        {result.map(search => (
        <div className="column" key={search.id} id="poster">
            <div className="card text-white bg-dark mb-3">
                <div className="card-header">{search.Title} ({search.Year})</div>
                  <div className="container">
                    <h4 className="card-title"></h4>
                    <img src={search.Poster} id="image"/>
                  </div>
                  <p class="card-text">{search.Type}</p>
                  <button onClick={() => addFavorite(search)}>Fav</button>
                  <button onClick={() => check(search)}>Ce</button>

            </div>
        </div>
        ))}
        </div>
        </center>
        <style jsx>{`
          .form-control {
            margin-right:12px;

          }
          .card-header {
            font-size: 14px;
          }
          #image {
            width: 100%;
            height: 300px
          }
          #poster {
            padding: 0 10px;
          }
          .card {
            width:250px;
            height:420px;
            text-align: center;
          }
          @media screen and (max-width: 600px) {
          .column {
            width: 100%;
            display: block;
            margin-bottom: 20px;
          }
        }

        `}</style>
    </div>
    );
};

export default Index;
