import {useEffect, useState } from 'react';
import { autorun } from 'mobx';
import localStorage from 'mobx-localstorage';

function favorite() {
  const initialState = () =>
    window.localStorage.getItem("count") || null;
  const [count, setCount] = useState(initialState);

  const increment = () => setCount(count + 1);
  const decrement = () => setCount(count - 1);

  useEffect(() => localStorage.setItem("count", count), [count]);

  return { count, increment, decrement };
}


const Favorites = props => {

  const [data, setData] = useState([]);

  useEffect(() => {
    const data = (JSON.parse(localStorage.getItem("data")));
    const favor = localStorage.getItem("fav");

    console.log(favor);    
    function filterData(data, filters) {
      console.log(data, filters);
      let favorite =  data.filter(({show}) => filters.includes(show.id));
      return favorite;
    }

    setData(filterData(data, favor));   


  }, [props]); // props triger


  return(
    <div className="container">
      <center>
        <div className="row">
        {data.map(({show}) => (
            <div className="column" key={show.id} id="poster">
                <div className="card text-white bg-primary mb-4">
                    <div className="card-header">{show.name}
                        <h4 className="card-title">{show.type}</h4>
                        <img src={show.image.medium} />  
                    </div>
                </div>
            </div>
            ))}
        </div>
      </center>
    <style jsx>{`
          .form-control {
            margin-right:12px;
          }
          #poster {
            padding: 0 10px;
          }
          .card {
            width:250px;
            height:420px;
            text-align: center;
          }
          @media screen and (max-width: 600px) {
          .column {
            width: 100%;
            display: block;
            margin-bottom: 20px;
          }
        }

        `}</style>
  </div>
  )};


export default Favorites;
