import localStorage from 'mobx-localstorage';
import React, { useState, useEffect } from 'react';

function Card({item}) {

    const [favorite, setFavorite] = useState([]);
    const [favstatus, setFavStatus] = useState(true);

    if(Array.isArray(favorite)){

        let isFav = favorite.find(items => item.imdbID === items.imdbID);

        if(isFav){
            setFavStatus(false);
        }
    }

    useEffect(() => {
        setFavorite(JSON.parse(localStorage.getItem('favorites')));



    }, []);

    return <div className="column" key={item.id} id="poster">
            <div className="card text-white bg-dark mb-3">
                <div className="card-header">{item.Title} ({item.Year})</div>
                  <div className="container">
                    <h4 className="card-title"></h4>
                    <img src={item.Poster} id="image"/>
                  </div>
                  <p className="card-text">{item.Type}</p>
                  <button style={{display:favstatus ? "":"none"}} onClick={() => check(item)}>Fav</button>
            </div>
            <style jsx>{`
                  .card-header {
                    font-size: 14px;
                  }
                  #image {
                    width: 100%;
                    height: 300px
                  }
                  #poster {
                    padding: 0 10px;
                  }
                  .card {
                    width:250px;
                    height:420px;
                    text-align: center;
                  }
                  @media screen and (max-width: 600px) {
                  .column {
                    width: 100%;
                    display: block;
                    margin-bottom: 20px;
                  }
                }
            `}
            </style>

        </div>;
}

export default Card;


