import {useEffect, useState } from 'react';
import { autorun } from 'mobx';
import localStorage from 'mobx-localstorage';

const Favorites = props => {

  const [data, setData] = useState([]);

  useEffect(() => {
    const data = (JSON.parse(localStorage.getItem("data")));
    console.log("data", data);
    function filterData(data, filters) {
      return data.filter((item) => filters.includes(item.id));
    }

    console.log(filterData(data, [975]));
    setData(data); 
  }, [props]); // props triger

  return(
    <div className="container">
      <center>
        <div className="row">
        {data.map(({show}) => (
            <div className="column" key={show.id} id="poster">
                <div className="card text-white bg-primary mb-4">
                    <div className="card-header">{show.name}
                        <h4 className="card-title">{show.type}</h4>
                        <img src={show.image.medium} />  
                    </div>
                </div>
            </div>
            ))}
        </div>
      </center>
    <style jsx>{`
          .form-control {
            margin-right:12px;
          }
          #poster {
            padding: 0 10px;
          }
          .card {
            width:250px;
            height:420px;
            text-align: center;
          }
          @media screen and (max-width: 600px) {
          .column {
            width: 100%;
            display: block;
            margin-bottom: 20px;
          }
        }

        `}</style>
  </div>
  )};


export default Favorites;
