import fetch from 'isomorphic-unfetch';
import localStorage from 'mobx-localstorage';
import React, { useState, useEffect } from 'react';

function TodoForm({addTodo}) {
  const [value, setValue] = useState('');

  const handleSubmit = e => {
    e.preventDefault();
    if(!value) return;
    addTodo(value);
    setValue('');
  }

  return (
    <form onSubmit={handleSubmit}>
      <input type="text"
       className="input" 
       value={value} 
       placeholder="Add Todo..."
       onChange={e => setValue (e.target.value)}/>
    </form>
  )
}


const Index = props => {

  const [value, setValue] = useState();
  const [result, setResult] = useState([]);

  const handleSubmit = async e => {
    e.preventDefault();
    if(!value) return;

    const res = await fetch(`http://www.omdbapi.com/?s=${value}&apikey=8ae8b189`);
    const data = await res.json();

    setResult(data);

  }
    return (
      <div className="container">
        <form className="form-inline" id="form" onSubmit={handleSubmit}>
          <div className="row">
            <div className="column">
              <input type="text" className="form-control" placeholder="Title" onChange={e => setValue(e.target.value)}/>
            </div>
            <div className="column">
              <input type="text" className="form-control" placeholder="Year" />
            </div>
            <div className="column">
              <select id="inputState" className="form-control">
                <option selected>Choose...</option>
                <option>...</option>
              </select>
            </div>
            <button type="submit" className="btn btn-info">Search</button>
          </div>
        </form>
        <hr/>
        <center>
        <div className="row">
        {result(({search}) => (
        <div className="column" key={search.id} id="poster">
            <div className="card text-white bg-primary mb-4">
                <div className="card-header">{search.Title}
                    <h4 className="card-title">{show.Type}</h4>
                    <img src={search.Poster} />
                </div>
            </div>
        </div>
        ))}
        </div>
        </center>


        <style jsx>{`
          .form-control {
            margin-right:12px;
          }
          #poster {
            padding: 0 10px;
          }
          .card {
            width:250px;
            height:420px;
            text-align: center;
          }
          @media screen and (max-width: 600px) {
          .column {
            width: 100%;
            display: block;
            margin-bottom: 20px;
          }
        }

        `}</style>

    </div>

    );


};


Index.getInitialProps = async function () {


  const res = await fetch('https://api.tvmaze.com/search/shows?q=batman');
  const data = await res.json();

  const fav = [975, 481, 504]
  console.log(`Show data fetched. Count: ${data.length}`);

  if (typeof window !== 'undefined') {
    localStorage.setItem('data', JSON.stringify(data));
    localStorage.setItem('fav', fav);
  }

  return {
    shows: data.map(entry => entry.show)
  };

}



export default Index;
