import {useEffect, useState } from 'react';
import { autorun } from 'mobx';
import localStorage from 'mobx-localstorage';

var tableData = [
  {color: 'red', type:'fruit', name:'apple'},
  {color: 'blue', type:'fruit', name:'blueberry'},
  {color: 'green', type:'veg', name:'lettuce'},
  {color: 'green', type:'veg', name:'celery'},
];

function filterData(tableData, filters) {
 return tableData.filter(item => filters.includes(item.color));
}

console.log(filterData(tableData, ['red', 'green']));
const Favorites = props => {

  const [data, setData] = useState([]);

  useEffect(() => {
    const data = (JSON.parse(localStorage.getItem("data")));
    setData(data); 
  }, [props]); // props triger

  return(
  <div className="container">
    <div className="row">
    {data.map(({show}) => (
        <div className="column" key={show.id} id="poster">
            <div className="card text-white bg-primary mb-4">
                <div className="card-header">{show.name}
                    <h4 className="card-title">{show.type}</h4>
                    <img src={show.image.medium} />  
                </div>
            </div>
        </div>
        ))}
    </div>
    <style jsx>{`
          .form-control {
            margin-right:12px;
          }
          #poster {
            padding: 0 10px;
          }
          .card {
            width:250px;
            height:420px;
            text-align: center;
          }
          @media screen and (max-width: 600px) {
          .column {
            width: 100%;
            display: block;
            margin-bottom: 20px;
          }
        }

        `}</style>
  </div>
  )};


export default Favorites;
